    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>text</span> <span class="token attr-name">variable</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>citation-number<span class="token punctuation">&quot;</span></span><span class="token punctuation">/&gt;</span></span>
