import{_ as t}from"./chunks/TranslatorDetails.vue_vue_type_script_setup_true_lang.DPJwtk8Y.js";import{x as r,A as a,L as i,v as o}from"./chunks/framework.DNV5qdj1.js";import"./chunks/theme.B7B2fq_j.js";/* empty css                                    *//* empty css                      */import"./chunks/attachment-pdf.Po2993qH.js";import"./chunks/attachment-snapshot.BTFN8BBg.js";import"./chunks/localize.vYtg6Ra3.js";const y=JSON.parse('{"title":"转换器详情","description":"","frontmatter":{"title":"转换器详情","sidebar":false,"aside":false,"editLink":false,"outline":false,"prev":{"text":"返回转换器列表","link":"./"},"next":false,"comments":false},"headers":[],"params":{"translatorID":"d611008a-850d-4860-b607-54e1ecbcc592","translator":{"header":{"translatorID":"d611008a-850d-4860-b607-54e1ecbcc592","label":"E-Tiller","creator":"jiaojiaodubai","target":"","minVersion":"5.0","maxVersion":"","priority":200,"inRepository":true,"translatorType":4,"browserSupport":"gcsibv","lastUpdated":"2024-09-30 10:33:44"},"testCases":[{"type":"web","url":"http://zgnjzz.ijournals.cn/zgnjzz/article/abstract/202311001","items":[{"itemType":"journalArticle","title":"结肠镜检查时机对急性下消化道出血患者预后影响的Meta分析","creators":[{"firstName":"","lastName":"柏李一","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"姜维","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"程芮","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"闵力","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"张澍田","creatorType":"author","fieldMode":1}],"date":"2023-11-30","DOI":"10.12235/E20220711","ISSN":"1007-1989","abstractNote":"目的 系统评价结肠镜检查时机对急性下消化道出血（LGIB）患者预后的影响。方法 计算机检索PubMed、Medline、Embase、The Cochrane Library、维普、中国知网和万方数据自建库到2022年9月，结肠镜检查时机对急性LGIB患者预后影响的相关临床研究。由两名研究者独立筛选文献，提取信息，并进行偏倚风险评价后，采用RevMan 5.4软件对急诊结肠镜组和常规结肠镜组的再出血率、内镜下止血成功率、出血来源定位率和总体死亡率等多方面进行Meta分析。结果 共纳入12篇回顾性研究和4篇随机对照试验（RCT），包括400 180例患者。回顾性研究的Meta分析结果显示，急诊结肠镜在以下４个方面优于常规结肠镜：内镜下止血成功率（OR＾ = 1.64，95%CI：1.07～2.52，P = 0.020）、住院天数（MD = -1.67，95%CI：-2.92～-0.42，P = 0.009）、外科手术率（OR＾ = 0.52，95%CI：0.42～0.64，P = 0.000）和输血率（OR＾ = 0.83，95%CI：0.78～0.88，P = 0.000）；两组患者再出血率、总体死亡率、介入治疗率和出血来源定位率比较，差异均无统计学意义（P > 0.05）。RCT的Meta分析结果显示，急诊结肠镜内镜下止血成功率（OR＾ = 1.74，95%CI：1.04～2.93，P = 0.040）和出血来源定位率（OR＾ = 2.31，95%CI：1.28～4.17，P = 0.006）明显高于常规结肠镜，两组间其他结局指标比较，差异均无统计学意义（P > 0.05）。结论 与常规结肠镜相比，急诊结肠镜并不能降低再出血率和总体死亡率，但能够提高内镜下止血成功率，并在一定程度上缩短了住院天数，降低了外科手术率和输血率，在临床中需根据实际情况进行时机选择。","extra":"original-title: Effect of colonoscopy timing on clinical outcomes of patients with acute lower gastrointestinal bleeding: a Meta-analysis\\ncreatorsExt: [{\\"firstName\\":\\"\\",\\"lastName\\":\\"柏李一\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1,\\"original\\":\\"Bai Liyi\\"},{\\"firstName\\":\\"\\",\\"lastName\\":\\"姜维\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1,\\"original\\":\\"Jiang Wei\\"},{\\"firstName\\":\\"\\",\\"lastName\\":\\"程芮\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1,\\"original\\":\\"Cheng Rui\\"},{\\"firstName\\":\\"\\",\\"lastName\\":\\"闵力\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1,\\"original\\":\\"Min Li\\"},{\\"firstName\\":\\"\\",\\"lastName\\":\\"张澍田\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1,\\"original\\":\\"Zhang Shutian\\"}]","issue":"11","journalAbbreviation":"中国内镜杂志","language":"zh-CN","libraryCatalog":"zgnjzz.ijournals.cn","pages":"1-12","publicationTitle":"中国内镜杂志","url":"http://www.zgnjzz.com//zgnjzz/article/abstract/202311001","volume":"29","attachments":[{"title":"Full Text PDF","mimeType":"application/pdf"}],"tags":[{"tag":"Meta分析"},{"tag":"再出血"},{"tag":"常规结肠镜"},{"tag":"急性下消化道出血"},{"tag":"急诊结肠镜"}],"notes":[]}]},{"type":"web","url":"http://www.yaoxueyanjiu.com/ch/reader/view_abstract.aspx?file_no=202311005&flag=1","items":[{"itemType":"journalArticle","title":"多指标综合评价结合层次分析法优化黄芩微波酒制工艺","creators":[{"firstName":"","lastName":"李利华","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"王巍","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"张一美","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"赵梦辉","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"鞠成国","creatorType":"author","fieldMode":1}],"date":"2023-12-11","DOI":"10.13506/j.cnki.jpr.2023.11.005","abstractNote":"目的 采用正交设计综合加权评分和层次分析法相结合的方法，优化黄芩微波酒制工艺。方法 采用单因素试验优选加酒量、闷润时间、微波功率、微波时间，然后以微波酒制工艺中的加酒量、闷润时间、微波功率、微波时间为考察因素，采用层次分析法确定各指标权重系数，以黄芩苷、汉黄芩苷、黄芩素、汉黄芩素含量进行综合加权评分为评价指标，优化黄芩微波酒制工艺。结果 取生黄芩饮片适量，加入10%辅料酒，置密闭容器内闷润60 min，在300 W功率下微波5 min，经三次验证实验，各指标成分的平均含量分别为15.65%，5.94%，3.00%，0.33%，平均评分98.24分，RSD为0.99%（n=3）。结论 优化所得微波炮制酒工艺稳定，重复性良好，可应用于微波酒黄芩的炮制。","extra":"creatorsExt: [{\\"firstName\\":\\"\\",\\"lastName\\":\\"李利华\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1,\\"original\\":\\"\\"},{\\"firstName\\":\\"\\",\\"lastName\\":\\"王巍\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1},{\\"firstName\\":\\"\\",\\"lastName\\":\\"张一美\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1},{\\"firstName\\":\\"\\",\\"lastName\\":\\"赵梦辉\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1},{\\"firstName\\":\\"\\",\\"lastName\\":\\"鞠成国\\",\\"creatorType\\":\\"author\\",\\"fieldMode\\":1}]","issue":"11","language":"zh-CN","libraryCatalog":"www.yaoxueyanjiu.com","pages":"875-880","publicationTitle":"药学研究","url":"http://www.yaoxueyanjiu.com/ch/reader/view_abstract.aspx?file_no=202311005&flag=1","volume":"42","attachments":[{"title":"Snapshot","mimeType":"text/html"},{"title":"Full Text PDF","mimeType":"application/pdf"}],"tags":[{"tag":"层次分析法"},{"tag":"微波炮制工艺"},{"tag":"正交设计"},{"tag":"酒黄芩"},{"tag":"黄芩"}],"notes":[]}]},{"type":"web","url":"http://hkfdj.ijournals.cn/hkfdj/ch/reader/view_abstract.aspx?file_no=20160403&flag=1","items":[{"itemType":"journalArticle","title":"基于模型的系统工程概述","creators":[{"firstName":"","lastName":"朱静","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"杨晖","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"高亚辉","creatorType":"author","fieldMode":1},{"firstName":"","lastName":"姚太克","creatorType":"author","fieldMode":1}],"date":"2016","abstractNote":"由于航空领域涉及的系统日益高度复杂，为更好推进基于模型的系统工程（Model Based System Engineering ,MBSE）研发体系，通过从当前遇到的问题、推行基于模型的系统工程的必要性、优势、未来的挑战等方面进行了较为详细地阐述。基于模型的系统工程研发体系具有知识表示的无二义、系统设计的一体化、沟通交流的高效率等优势，是未来发展的大趋势。","extra":"original-title: Summary of Model Based System Engineering\\nview: 9490\\ndownload: #PDFClickNum","issue":"4","language":"zh-CN","libraryCatalog":"E-Tiller","pages":"12-16","publicationTitle":"航空发动机","url":"http://hkfdj.ijournals.cn/hkfdj/ch/reader/view_abstract.aspx?file_no=20160403&flag=1","volume":"42","attachments":[{"title":"Full Text PDF","mimeType":"application/pdf"}],"tags":[{"tag":"模型"},{"tag":"研发体系"},{"tag":"系统工程"},{"tag":"航空发动机"}],"notes":[]}]},{"type":"web","url":"http://www.stae.com.cn/jsygc/home","items":"multiple"},{"type":"web","url":"http://www.jishujingji.cn/technology_economics/home?id=2009081390342001&name=%E9%A6%96%E9%A1%B5","items":"multiple"},{"type":"web","url":"http://www.stae.com.cn/jsygc/home","items":"multiple"},{"type":"web","url":"http://www.qxkj.net.cn/","items":"multiple"},{"type":"web","url":"http://wkdxb.wust.edu.cn/wkd_zr/ch/index.aspx","items":"multiple"},{"type":"web","url":"http://wltb.ijournal.cn/ch/index.aspx","items":"multiple"}],"zhLabel":"勤云科技","trends":[{"author":"jiaojiaodubai","date":"2024-09-30 18:35:05 +0800","message":"close #365"},{"author":"jiaojiaodubai","date":"2024-06-20 17:04:42 +0800","message":"clean EOL, fix #351, fix #352"},{"author":"jiaojiaodubai","date":"2024-02-09 17:13:34 +0800","message":"按照官方指南更改优先级"}],"fileName":"E-Tiller.js"}},"relativePath":"translators/d611008a-850d-4860-b607-54e1ecbcc592.md","filePath":"translators/[translatorID].md"}'),l={name:"translators/d611008a-850d-4860-b607-54e1ecbcc592.md"},N=Object.assign(l,{setup(s){return(m,e)=>(o(),r("div",null,[e[0]||(e[0]=a("p",null,[a("a",{href:"./"},"返回转换器列表")],-1)),i(t)]))}});export{y as __pageData,N as default};
