import{_ as a}from"./chunks/TranslatorDetails.vue_vue_type_script_setup_true_lang.DPJwtk8Y.js";import{x as i,A as t,L as o,v as n}from"./chunks/framework.DNV5qdj1.js";import"./chunks/theme.B7B2fq_j.js";/* empty css                                    *//* empty css                      */import"./chunks/attachment-pdf.Po2993qH.js";import"./chunks/attachment-snapshot.BTFN8BBg.js";import"./chunks/localize.vYtg6Ra3.js";const b=JSON.parse(`{"title":"转换器详情","description":"","frontmatter":{"title":"转换器详情","sidebar":false,"aside":false,"editLink":false,"outline":false,"prev":{"text":"返回转换器列表","link":"./"},"next":false,"comments":false},"headers":[],"params":{"translatorID":"dd9efb0b-ca1d-4634-b480-9aabc84213c0","translator":{"header":{"translatorID":"dd9efb0b-ca1d-4634-b480-9aabc84213c0","label":"WeiPu","creator":"Xingzhong Lin","target":"^https?://(lib|qikan)\\\\.cqvip\\\\.com","minVersion":"3.0","maxVersion":"","priority":100,"inRepository":true,"translatorType":4,"browserSupport":"gcsibv","lastUpdated":"2024-01-04 10:42:39"},"testCases":[{"type":"web","url":"https://lib.cqvip.com/Qikan/Article/Detail?id=7109808542&from=Qikan_Search_Index","items":[{"itemType":"journalArticle","title":"数字时代背景下在线诉讼的发展路径与风险挑战","creators":[{"lastName":"刘峥","creatorType":"author","fieldMode":1}],"date":"2023","abstractNote":"在线诉讼是互联网时代的必然产物,它可能存在发展快或慢的问题,但它的到来不可避免且无法抗拒,其存在的必要性与合理性也是母庸置疑的。现行民事诉讼法已对在线诉讼的效力予以确认。应当明确,基于诉讼活动的特质和规律,目前,在线诉讼只是线下诉讼的有益补充,并非取而代之。本文从《人民法院在线诉讼规则》出发,论述了在线诉讼的时代背景和发展历程,阐明在线诉讼的程序规范性、权利保障性、方式便捷性、模式融合性。同时,在线诉讼将对未来司法制度的完善发展产生巨大推动作用,在理论更新、规则指引、制度完善、技术迭代、安全保障、人才培养等方面均需作出必要的配套跟进。","extra":"original-title: Navigating Online Litigation in the Digital Age:Paths and Challenges\\nabstractTranslation: The emergence of online litigation is an inevitable corollary of the Internet era.Although the speed of its development may vary,the necessity and rationality of its existence are indisputable.The Civil Procedure Law of the People's Republic of China has confirmed the legal effects of online litigation.Notably,online litigation currently serves as a complementary tool rather than a substitution for offline litigation,given the nature and law of litigation.Drawing on the Online Litigation Rules of the People's Courts,this article examines online litigation's historical background and development.We illustrate how online litigation standardizes procedures,protects litigants'rights,provides convenience to court users,and integrates different modes of litigation.Furthermore,we argue that online litigation can serve as a driving force behind the future advancement of the judicial system.To achieve this,measures such as theoretical innovation,rules-based guidance,institutional improvement,technological iteration,and talent cultivation need to be implemented.FEWER","issue":"2","libraryCatalog":"WeiPu","pages":"122-135","publicationTitle":"数字法治","url":"https://lib.cqvip.com/Qikan/Article/Detail?id=7109808542&from=Qikan_Search_Index","attachments":[],"tags":[{"tag":"在线诉讼"},{"tag":"基本特征"},{"tag":"融合发展"},{"tag":"风险挑战"}],"notes":[]}]}],"zhLabel":"维普","trends":[{"author":"jiaojiaodubai","date":"2024-06-20 17:04:42 +0800","message":"clean EOL, fix #351, fix #352"},{"author":"jiaojiaodubai","date":"2024-01-04 18:43:24 +0800","message":"更新内部函数；规范字段；更新测试用例"},{"author":"l0o0","date":"2023-11-02 22:54:52 +0800","message":"fix: 在pr227的基础上，做了部分修改并添加测试示例. closes #70, closes #90, closes #173"}],"fileName":"WeiPu.js"}},"relativePath":"translators/dd9efb0b-ca1d-4634-b480-9aabc84213c0.md","filePath":"translators/[translatorID].md"}`),r={name:"translators/dd9efb0b-ca1d-4634-b480-9aabc84213c0.md"},v=Object.assign(r,{setup(s){return(l,e)=>(n(),i("div",null,[e[0]||(e[0]=t("p",null,[t("a",{href:"./"},"返回转换器列表")],-1)),o(a)]))}});export{b as __pageData,v as default};
