import{ai as r,x as s,aE as b,L as t,aa as i,v as l}from"./chunks/framework.DNV5qdj1.js";const p="/assets/bib-%E9%A6%96%E9%A1%B5.BdhVQVFK.png",n="/assets/bib-%E9%80%89%E6%8B%A9%E6%A0%B7%E5%BC%8F.j4dDVv2L.png",c="/assets/bib-%E5%A4%8D%E5%88%B6%E7%BB%93%E6%9E%9C.MSRUloWE.png",f=JSON.parse('{"title":"使用 Zotero bib 生成参考文献表","description":"","frontmatter":{"title":"使用 Zotero bib 生成参考文献表","date":"2023-06-28T21:14:03.000Z","updated":"2023-10-07T20:08:24.000Z"},"headers":[],"relativePath":"user-guide/create-bibliographies-via-zbib.md","filePath":"wiki/user-guide/create-bibliographies-via-zbib.md"}'),d={name:"user-guide/create-bibliographies-via-zbib.md"};function g(m,e,h,_,u,z){const o=i("NolebaseGitContributors"),a=i("NolebaseGitChangelog");return l(),s("div",null,[e[0]||(e[0]=b('<h1 id="使用-zotero-bib-生成参考文献表" tabindex="-1">使用 Zotero bib 生成参考文献表 <a class="header-anchor" href="#使用-zotero-bib-生成参考文献表" aria-label="Permalink to &quot;使用 Zotero bib 生成参考文献表&quot;">​</a></h1><p>Zotero bib 是 Zotero 官方维护的一个引文生成器，使用它，你可以不下载 Zotero，或不将条目导入 Zotero 库，即可生成对应的引用格式。</p><p>地址：<a href="https://zbib.org/" target="_blank" rel="noreferrer">https://zbib.org/</a></p><div class="warning custom-block"><p class="custom-block-title">警告</p><p>这种方法生成的引文是纯文本，无法更新，仅适用于临时使用的场景。例如某文档确定只需要引几篇文档，且不想导入这几篇文档到自己的库。</p></div><p>使用：</p><ol><li><p>打开 <a href="https://zbib.org/" target="_blank" rel="noreferrer">https://zbib.org/</a></p></li><li><p>在下图输入框中输入需要引用的条目链接/识别符等，然后点击「Cite」</p><p><img src="'+p+'" alt="zbib首页" loading="lazy"></p></li><li><p>该条条目将会显示如下引用信息，你可以在下图红框处选择样式。</p><p><img src="'+n+'" alt="zbib选择样式" loading="lazy"></p></li><li><p>继续重复第 2 步可以添加多个条目</p></li><li><p>已添加的条目将会存储在浏览器，下次打开网页仍然可以看到，直到你清除浏览器 Cookie 或手动删除。</p></li><li><p>点击「Copy to Clipboard」可以将结果复制到粘贴板。</p><p><img src="'+c+'" alt="zbib复制结果" loading="lazy"></p></li></ol>',6)),t(o),t(a)])}const B=r(d,[["render",g]]);export{f as __pageData,B as default};
