import{ai as d,x as i,aE as p,L as a,aa as e,v as c}from"./chunks/framework.DNV5qdj1.js";const s="/assets/caj2pdf-%E5%85%A8%E7%90%83%E5%AD%A6%E6%9C%AF%E5%BF%AB%E6%8A%A5%E6%89%93%E5%BC%80%E6%96%87%E7%8C%AE.Xl58a1nE.jpg",l="/assets/caj2pdf-%E5%85%A8%E7%90%83%E5%AD%A6%E6%9C%AF%E5%BF%AB%E6%8A%A5%E5%8F%A6%E5%AD%98%E4%B8%BA.Cbp98Si_.jpg",h="/assets/caj2pdf-caj2pdf.Ds1TRgih.jpg",n="/assets/caj2pdf-%E6%B2%B9%E7%8C%B4%E8%84%9A%E6%9C%AC%E4%B8%8B%E8%BD%BD%E6%96%87%E7%8C%AE.DreSvSPJ.jpg",A="/assets/caj2pdf-cajviewer%E6%89%93%E5%8D%B0.DI2KO4Vt.jpg",E="/assets/caj2pdf-cajviewer%E9%80%89%E6%8B%A9%E6%89%93%E5%8D%B0%E6%9C%BA.2wjd0qHW.jpg",f="data:image/jpeg;base64,",B=JSON.parse('{"title":"CAJ 文件转为 PDF 文件","description":"","frontmatter":{"title":"CAJ 文件转为 PDF 文件","authors":["Chikit_L"],"date":"2024-07-21T15:24:09.000Z","updated":"2024-07-21T15:24:09.000Z"},"headers":[],"relativePath":"user-guide/faqs/caj2pdf.md","filePath":"wiki/user-guide/faqs/caj2pdf.md"}'),m={name:"user-guide/faqs/caj2pdf.md"};function g(k,t,b,I,u,C){const r=e("NolebaseGitContributors"),o=e("NolebaseGitChangelog");return c(),i("div",null,[t[0]||(t[0]=p('<h1 id="caj-文件转为-pdf-文件" tabindex="-1">CAJ 文件转为 PDF 文件 <a class="header-anchor" href="#caj-文件转为-pdf-文件" aria-label="Permalink to &quot;CAJ 文件转为 PDF 文件&quot;">​</a></h1><h2 id="各方式优缺点对比" tabindex="-1">各方式优缺点对比 <a class="header-anchor" href="#各方式优缺点对比" aria-label="Permalink to &quot;各方式优缺点对比&quot;">​</a></h2><table tabindex="0"><thead><tr><th>方式</th><th>优点</th><th>缺点</th><th>推荐度</th></tr></thead><tbody><tr><td><a href="#全球学术快报">全球学术快报</a></td><td>无损转换，最好的方式</td><td>需要软件</td><td>⭐⭐⭐⭐⭐</td></tr><tr><td><a href="#caj2pdf-项目">caj2pdf 项目</a></td><td>无损转换</td><td>部分文献转换失败</td><td>⭐⭐⭐⭐</td></tr><tr><td><a href="#油猴脚本">油猴脚本</a></td><td>方便</td><td>脚本更新可能不及时</td><td>⭐⭐⭐</td></tr><tr><td><a href="#虚拟打印机转换">虚拟打印机转换</a></td><td>通用</td><td>体积大，无法复制文字</td><td>⭐</td></tr><tr><td><a href="#网页转换">网页转换</a></td><td>通用</td><td>收费</td><td>⭐</td></tr></tbody></table><h2 id="全球学术快报" tabindex="-1">全球学术快报 <a class="header-anchor" href="#全球学术快报" aria-label="Permalink to &quot;全球学术快报&quot;">​</a></h2><p>官网：<a href="https://m.cnki.net/mcnkidown/index.html" target="_blank" rel="noreferrer">https://m.cnki.net/mcnkidown/index.html</a></p><p>注册知网帐号 ：<a href="https://my.cnki.net/Register/CommonRegister.aspx?returnUrl=https://www.cnki.net" target="_blank" rel="noreferrer">https://my.cnki.net/Register/CommonRegister.aspx?returnUrl=https://www.cnki.net</a></p><p>进入 <code>资料库</code> - <code>打开更多</code>- 选择想要转换的 caj 文件</p><p><img src="'+s+'" alt="alt text" loading="lazy"></p><p>在文献界面 <code>右键</code> - <code>另存为</code>- 保存 pdf 至指定目录即可。</p><p><img src="'+l+'" alt="alt text" loading="lazy"></p><h2 id="caj2pdf-项目" tabindex="-1">caj2pdf 项目 <a class="header-anchor" href="#caj2pdf-项目" aria-label="Permalink to &quot;caj2pdf 项目&quot;">​</a></h2><p>最初的<a href="https://github.com/caj2pdf/caj2pdf" target="_blank" rel="noreferrer">caj2pdf</a>项目由于长时间未更新以及没有可视化窗口，不推荐继续使用，更推荐使用<a href="https://github.com/sainnhe/caj2pdf-qt" target="_blank" rel="noreferrer">caj2pdf-qt</a>项目（<a href="https://caj2pdf-qt.sainnhe.dev/" target="_blank" rel="noreferrer">镜像站</a>）</p><p>这是一个 GUI 版本的 CAJ 转 PDF 转换器，基于 <a href="https://github.com/caj2pdf/caj2pdf" target="_blank" rel="noreferrer">caj2pdf</a> 和 <a href="https://mupdf.com/" target="_blank" rel="noreferrer">mupdf</a> 实现。</p><p>特性：</p><p>✅ 支持 CAJ/KDH/NH 文件格式<br> ✅ 跨平台 (Windows 7, 8, 10, 11; macOS; Linux)<br> ✅ 支持 32 &amp; 64 位 CPU 架构<br> ✅ 批量转换<br> ✅ 多线程转换<br> ✅ 文件拖拽<br> 🔲 闭源引擎 (<a href="https://github.com/caj2pdf/caj2pdf/issues/63" target="_blank" rel="noreferrer">caj2pdf#63</a>)</p><p><img src="'+h+'" alt="alt text" loading="lazy"></p><p>软件的使用也非常简单，<code>输入文件</code> → 选择 <code>输出目录</code> → <code>开始转换</code>，等待数秒即可。部分 caj 文件会转换失败，失败的文件可尝试其他方法进行转换。</p><h2 id="油猴脚本" tabindex="-1">油猴脚本 <a class="header-anchor" href="#油猴脚本" aria-label="Permalink to &quot;油猴脚本&quot;">​</a></h2><p><a href="https://greasyfork.org/zh-CN/scripts/389343-%E4%B8%AD%E5%9B%BD%E7%9F%A5%E7%BD%91cnki%E7%A1%95%E5%8D%9A%E8%AE%BA%E6%96%87pdf%E4%B8%8B%E8%BD%BD" target="_blank" rel="noreferrer">中国知网 CNKI 硕博论文 PDF 下载</a></p><div class="info custom-block"><p class="custom-block-title">提醒</p><p>该方法依赖于浏览器的 <a href="https://www.tampermonkey.net/index.php?browser=chrome&amp;locale=zh" target="_blank" rel="noreferrer">Tampermonkey</a> 插件，如果您未安装，请先安装 <a href="https://www.tampermonkey.net/index.php?browser=chrome&amp;locale=zh" target="_blank" rel="noreferrer">Tampermonkey</a> 插件。</p></div><p><img src="'+n+'" alt="alt text" loading="lazy"></p><p>在搜索界面 点击 脚本的 <code>批量下载PDF</code> - <code>获取链接</code> - <code>PDF下载</code> - 下载对应的文章的 PDF 即可。</p><h2 id="虚拟打印机转换" tabindex="-1">虚拟打印机转换 <a class="header-anchor" href="#虚拟打印机转换" aria-label="Permalink to &quot;虚拟打印机转换&quot;">​</a></h2><p>该方法需要电脑安装有<a href="https://cajviewer.cnki.net/" target="_blank" rel="noreferrer">CAJViewer</a>（或其他 CAJ 阅读器）以及 PDF 阅读器。</p><div class="info custom-block"><p class="custom-block-title">提醒</p><p>在测试过程中，发现 CAJViewer 9.0 无法保存 PDF 文件。可以尝试使用 CAJViewer 7.2 来解决此问题。</p></div><p>1.使用 CAJViewer 打开 CAJ 文件，然后点击 <code>文件</code> - <code>打印</code></p><p><img src="'+A+'" alt="alt text" loading="lazy"></p><p>2.选择打印机。由于电脑上安装的 PDF 阅读器不同，虚拟打印机的名称也会有所不同。在此示例中，选择 <code>Microsoft Print to PDF</code> 。</p><p><img src="'+E+'" alt="alt text" loading="lazy"></p><p>3.选择保存位置后，等待转换完成即可。</p><p><img src="'+f+'" alt="alt text" loading="lazy"></p><div class="info custom-block"><p class="custom-block-title">提醒</p><p>所有扫描版的 PDF 文件都可以使用 OCR 软件将图片中的文字转换为可编辑文本。例如，你可以使用 <a href="https://github.com/hiroi-sora/Umi-OCR" target="_blank" rel="noreferrer">Umi-OCR</a> 或其他常用的 PDF 阅读器（Adobe Acrobat、ABBYY、福昕阅读器等）来完成这一操作</p></div><h2 id="网页转换" tabindex="-1">网页转换 <a class="header-anchor" href="#网页转换" aria-label="Permalink to &quot;网页转换&quot;">​</a></h2><p>由于大部分在线转换功能的网页都需要付费使用，在此不做推荐。</p>',34)),a(r),a(o)])}const R=d(m,[["render",g]]);export{B as __pageData,R as default};
