import{_ as g,a as u}from"./chunks/update-translators-文献详情页2.BmpOL-2x.js";import{ai as f,x as Z,A as t,a2 as r,L as a,aE as e,aa as i,v as C}from"./chunks/framework.DNV5qdj1.js";const b="/assets/item-in-zotero.DDYSUDDB.png",E="/assets/update-official-translators.CXHM-gsM.jpg",h="/assets/update-unofficial-translators.Di9wojM_.jpg",n="/assets/update-translator-chrome-1.DSg59B8y.jpg",l="/assets/update-translator-ResetTranslators.qtjjQygW.jpg",p="/assets/update-translator-safari-1.C8vkHQWT.jpg",c="/assets/update-translator-%E5%A6%82%E4%BD%95%E7%A1%AE%E4%BF%9D%E6%9B%B4%E6%96%B0%E6%88%90%E5%8A%9F.DSEzAELl.jpg",k="/assets/zotero-%E6%95%B0%E6%8D%AE%E5%82%A8%E5%AD%98%E4%BD%8D%E7%BD%AE.B7Gitgiu.png",z="/assets/%E6%95%B0%E6%8D%AE%E5%82%A8%E5%AD%98%E4%BD%8D%E7%BD%AEtranslators%E6%96%87%E4%BB%B6%E5%A4%B9.CDP8itE-.png",y="/assets/%E6%89%8B%E5%8A%A8%E5%9C%A8github%E4%B8%8B%E8%BD%BDtranslators.CHFNpTKL.png",B="/assets/translators%E6%96%87%E4%BB%B6.CvA0vAcn.png",v="/assets/%E6%89%8B%E5%8A%A8%E6%9B%B4%E6%96%B0translators.CoxIixHB.png",x="/assets/uninstall-connector-chrome.BUeXZvZx.jpg",A="/assets/uninstall-connector-chrome2.DPxCG86d.jpg",D="/assets/uninstall-connector-safari.BJa8Utno.jpg",_="/assets/uninstall-connector-safari1.C6IYHYAa.jpg",S="/assets/uninstall-connector-safari2.BWzOW6AA.jpg",P="/assets/uninstall-connector-safari3.Bq9Gp-Ow.jpg",T="/assets/connector-safari-button.CAVunT6s.jpg",j="/assets/connector-safari-button2.C9uWENao.jpg",X=JSON.parse('{"title":"抓取文献条目信息的常见问题","description":"","frontmatter":{"title":"抓取文献条目信息的常见问题","date":"2023-09-14T21:17:47.000Z"},"headers":[],"relativePath":"user-guide/faqs/update-translators.md","filePath":"wiki/user-guide/faqs/update-translators.md"}'),G={name:"user-guide/faqs/update-translators.md"},M={class:"table-of-contents"},F={href:"#方法-1-一键自动更新"},q={href:"#方法-2-手动替换转换器文件更新"},N={href:"#卸载并重装-zotero-connector-浏览器扩展"},w={href:"#使用其他添加条目的方式"},Q={id:"方法-1-一键自动更新",tabindex:"-1"},R={id:"方法-2-手动替换转换器文件更新",tabindex:"-1"},U={id:"卸载并重装-zotero-connector-浏览器扩展",tabindex:"-1"},H={id:"使用其他添加条目的方式",tabindex:"-1"};function V(W,o,$,L,O,Y){const s=i("Badge"),d=i("NolebaseGitContributors"),m=i("NolebaseGitChangelog");return C(),Z("div",null,[o[18]||(o[18]=t("h1",{id:"抓取文献条目信息的常见问题",tabindex:"-1"},[r("抓取文献条目信息的常见问题 "),t("a",{class:"header-anchor",href:"#抓取文献条目信息的常见问题","aria-label":'Permalink to "抓取文献条目信息的常见问题"'},"​")],-1)),o[19]||(o[19]=t("p",null,"本页面整理了一些抓取文献条目信息相关问题，你可以在右侧大纲栏（部分窄屏设备也可能在顶部「页面导航」）快速定位到你需要的内容。",-1)),o[20]||(o[20]=t("p",null,"如果仍没有找到你需要的问题，可以考虑在 Zotero 中文社区提问咨询或前往 Zotero 官方论坛发帖求助。",-1)),t("nav",M,[t("ul",null,[o[5]||(o[5]=t("li",null,[t("a",{href:"#基础知识-如何判断抓取是否成功"},"基础知识：如何判断抓取是否成功？")],-1)),t("li",null,[o[2]||(o[2]=t("a",{href:"#更新抓取文献信息的转换器"},"更新抓取文献信息的转换器",-1)),t("ul",null,[t("li",null,[t("a",F,[o[0]||(o[0]=r("方法 1：一键自动更新 ")),a(s,{text:"推荐"})])]),t("li",null,[t("a",q,[o[1]||(o[1]=r("方法 2：手动替换转换器文件更新 ")),a(s,{text:"高级"})])])])]),t("li",null,[t("a",N,[o[3]||(o[3]=r("卸载并重装 Zotero Connector 浏览器扩展 ")),a(s,{text:"中级"})])]),t("li",null,[t("a",w,[o[4]||(o[4]=r("使用其他添加条目的方式 ")),a(s,{text:"初级"})])])])]),o[21]||(o[21]=e('<h2 id="基础知识-如何判断抓取是否成功" tabindex="-1">基础知识：如何判断抓取是否成功？ <a class="header-anchor" href="#基础知识-如何判断抓取是否成功" aria-label="Permalink to &quot;基础知识：如何判断抓取是否成功？&quot;">​</a></h2><p>Zotero 文库中主要有三类内容： <strong>文献条目，附件文件，笔记</strong> 。其中，附件文件和笔记既可以附属于一篇文献的条目，也可以自己作为顶级项目独立存储在文库中。常见的条目类型以及常用的添加条目方法请阅读 <a href="./../add-items">添加条目教程</a> 。</p><div class="info custom-block"><p class="custom-block-title">一点说明</p><p>以下是文库中常见的几种情况： <img src="'+b+'" alt="文库中常见的几种情况" loading="lazy"></p><ul><li><strong>绿色</strong>的情况最完美，可以正常阅读文献也可以正常引用；</li><li><strong>橙色</strong>的情况有正确的文献信息，可以正常引用，在<a href="./../add-attachments#为条目添加附件">手动补充附件</a>后也可以在 Zotero 中阅读论文全文；</li><li><strong>红色</strong>的情况只有一个顶级附件，无法正常引用，必须为其<a href="./../add-items#通过附件添加条目-推荐">抓取</a>/<a href="./../add-items#手动创建条目-万能方法">创建</a>一个条目后才能正常引用。</li></ul></div><p>Zotero Connector 浏览器扩展在网页上抓取文献时通常可以得到两类内容： <strong>文献信息（文献条目的元数据）</strong> ，以及相应的 <strong>附件（如 PDF 全文、网页截图等）</strong> 。</p><ul><li>一般来说，只要你在网页上或 Zotero 里 <strong>能够正确地识别出文献类型</strong> （<a href="./../add-items#条目-item">条目图标</a> 与该文献的类型相对应），就已经意味着你的文献信息/元数据 <strong>抓取是成功的</strong> 。如果希望抓取的是「学位论文」，条目左边的图标却是「网页」或「期刊文章」，这通常意味着抓取失败或抓取到的部分信息不准确。</li><li>通过网页自动抓取 PDF 附件的功能在实际使用中比较玄学，可能会因为各种各样的限制无法自动抓到附件。 <strong>如果条目抓取正常，只是抓不到附件，通常是无解的。</strong></li></ul><p>正因如此， <strong>只要你能够正确抓取到文献信息，就不建议继续折腾下去了。如果抓不到 PDF 附件，建议直接手动下载后 <a href="./../add-attachments#为条目添加附件">添加到 Zotero 中抓取好的条目</a></strong> 。</p><div class="warning custom-block"><p class="custom-block-title">⚠ 避免批量抓取文献</p><p>无论何时，请避免进行大量的批量抓取操作，严重时可能导致封禁！</p></div><div class="info custom-block"><p class="custom-block-title">尽量在文献详情页抓取</p><p>在文献搜索界面或阅读界面进行条目抓取非常容易失败，请在文献的详情页（如下）进行条目的抓取。</p><details class="details custom-block"><summary>典型的详情页示例</summary><p><img src="'+g+'" alt="文献详情页" loading="lazy"></p><p><img src="'+u+'" alt="文献详情页" loading="lazy"></p></details></div><div class="tip custom-block"><p class="custom-block-title">ScienceDirect 需要手动完成反爬虫验证</p><p>ScienceDirect 增强了反爬虫机制，这限制了 Zotero 的抓取功能，Zotero 7 中针对该措施推出了一项新功能。当你尝试抓取 ScienceDirect 上的文章时，Zotero 会显示 ScienceDirect 的验证码页面。如果你完成了验证码，Zotero 则会完成 PDF 的下载。</p></div><p>如果你不能正确地抓取文献信息，或者抓取后的条目里文献信息缺失，那么下面这篇文档会对你有所帮助。</p><h2 id="更新抓取文献信息的转换器" tabindex="-1">更新抓取文献信息的转换器 <a class="header-anchor" href="#更新抓取文献信息的转换器" aria-label="Permalink to &quot;更新抓取文献信息的转换器&quot;">​</a></h2><p>在使用 Zotero 的过程中，我们经常需要更新 <code>转换器</code> 来实现从网页快速抓取文献信息。通常，我们可以借助 Zotero 本身的功能以及 茉莉花 插件快速完成该操作。但当 Zotero 或 茉莉花 插件出现故障无法完成一键自动更新时，我们也可以手动下载新版的转换器文件，并进行手动更新。</p><div class="info custom-block"><p class="custom-block-title">提醒</p><p>这里的「转换器」指的是 Zotero 从浏览器网页抓取文献信息时所需的转换器，在 Zotero 中有时也被称作是「翻译器」或「translator」。如果你遇到的是语言翻译问题（如标题翻译、摘要翻译、文献阅读中的句段翻译等），通常与本文讲的转换器（translator）无关，请参照 Translate for Zotero 插件的教程进行排查。</p></div>',13)),t("h3",Q,[o[6]||(o[6]=r("方法 1：一键自动更新 ")),a(s,{text:"推荐"}),o[7]||(o[7]=r()),o[8]||(o[8]=t("a",{class:"header-anchor",href:"#方法-1-一键自动更新","aria-label":`Permalink to "方法 1：一键自动更新 <Badge text='推荐' />"`},"​",-1))]),o[22]||(o[22]=e('<h4 id="步骤-1-更新官方转换器" tabindex="-1">步骤 1. 更新官方转换器 <a class="header-anchor" href="#步骤-1-更新官方转换器" aria-label="Permalink to &quot;步骤 1. 更新官方转换器&quot;">​</a></h4><p>在 <code>Zotero 设置</code> 中，进入 <code>高级</code> 设置，点击下方「自动检查转换器和样式的更新」后面的 <code>立即更新</code> 按钮。</p><p><img src="'+E+'" alt="更新官方转换器" loading="lazy"></p><div class="tip custom-block"><p class="custom-block-title">推荐保持自动更新</p><p>我们推荐保持勾选 「自动检查转换器和样式的更新」 以获得最新的官方转换器。</p></div><div class="warning custom-block"><p class="custom-block-title">「更新转换器」 和 「重置转换器」 是不同的！</p><p>注意：如无必要，不要点设置页下面的「数据库维护 -&gt; 重置转换器 (Reset Translators)」 按钮，这会删除整个 translators 文件夹。</p></div><h4 id="步骤-2-更新中文转换器" tabindex="-1">步骤 2. 更新中文转换器 <a class="header-anchor" href="#步骤-2-更新中文转换器" aria-label="Permalink to &quot;步骤 2. 更新中文转换器&quot;">​</a></h4><ol><li><p>安装/更新茉莉花插件</p><p>茉莉花插件是一个 Zotero 中文生态增强插件，提供了中文转换器的更新服务，请确保你安装了最新版本的茉莉花插件，浏览 <a href="./../plugins/jasminum">茉莉花</a> 了解详情。</p></li><li><p>进入茉莉花插件的设置，转到 「非官方维护中文转换器（翻译器）」 部分，然后点击 「更新全部」 按钮。</p><p><img src="'+h+'" alt="更新「非官方维护中文转换器」" loading="lazy"></p><div class="info custom-block"><p class="custom-block-title">提醒</p><p>建议不定期来这里更新一下非官方维护中文转换器（翻译器）。特别是在遇到中文文献无法抓取的时候，通常意味着这里的转换器需要更新了。</p></div></li></ol><h4 id="步骤-3-更新-zotero-connecter-的缓存" tabindex="-1">步骤 3. 更新 Zotero Connecter 的缓存 <a class="header-anchor" href="#步骤-3-更新-zotero-connecter-的缓存" aria-label="Permalink to &quot;步骤 3. 更新 Zotero Connecter 的缓存&quot;">​</a></h4><ol><li><p>更新 <strong>每一个浏览器</strong> 中 Zotero Connector 扩展里的转换器（translators）。</p><div class="tip custom-block"><p class="custom-block-title">提示</p><p>从 Zotero Connector v5.0.124 开始，官方移除了 Zotero Connector 设置里 「Advanced」-&gt;「Translators」 中的 <code>Update Translaors</code> 按钮，如果你的浏览器扩展中仍然保留 <code>Update Translaors</code> 按钮，请务必先<a href="./../install#浏览器扩展-zotero-connector">升级 Zotero Connector 浏览器扩展</a>再进行后续操作。</p></div><p>请根据您使用的浏览器点开下面相应的说明，并按照说明中的步骤操作。（360（极速）浏览器、搜狗浏览器、QQ 浏览器等基于 Chromium 等国内厂商推出的浏览器请参照 Google Chrome 的步骤操作）</p><details class="details custom-block"><summary>Google Chrome、 Microsoft Edge 和 Mozilla Firefox</summary><ol><li><p>右键点击 Zotero Connector 按钮，然后点击 选项/Preference</p><p><img src="'+n+'" alt="打开 Zotero Connector 的选项" loading="lazy"></p></li><li><p>点击 Advanced 中的 Reset Translators 按钮</p><p><img src="'+l+'" alt="更新 Zotero Connector 中的 translators" loading="lazy"></p></li></ol></details><details class="details custom-block"><summary>Apple Safari</summary><p>虽然 Zotero 目前已经支持在 Safari 中使用，但实际使用中在 Safari 中抓取失败的案例比较多。 **建议使用 Microsoft Edge、Google Chrome 或 Mozilla Firefox 浏览器进行抓取。</p><ol><li><p>在网页空白处点鼠标右键，然后点击 Zotero Preference</p><p><img src="'+p+'" alt="打开 Zotero Connector 的选项" loading="lazy"></p></li><li><p>点击 Advanced 中的 Reset Translators 按钮</p><p><img src="'+l+'" alt="更新 Zotero Connector 中的 translators" loading="lazy"></p></li></ol></details><div class="warning custom-block"><p class="custom-block-title">警告</p><p><strong>这一步骤非常关键！</strong> 请务必确保为 <strong>每一个浏览器</strong> 中的 Zotero Connector 扩展更新转换器！</p><p>点击 Reset Translators 按钮时，你也可以通过检查日志的方式确保转换器成功更新</p><p><img src="'+c+'" alt="通过日志确保转换器成功更新" loading="lazy"></p></div></li><li><p>重启浏览器。</p></li><li><p>此时你已经完成了转换器的更新。此时如果你的浏览器仍然不能完成对文献的识别，在确保步骤 1-2 正确的前提下，可重复几次步骤 3 。</p></li></ol>',9)),t("h3",R,[o[9]||(o[9]=r("方法 2：手动替换转换器文件更新 ")),a(s,{text:"高级"}),o[10]||(o[10]=r()),o[11]||(o[11]=t("a",{class:"header-anchor",href:"#方法-2-手动替换转换器文件更新","aria-label":'Permalink to "方法 2：手动替换转换器文件更新 <Badge text="高级" />"'},"​",-1))]),o[23]||(o[23]=e('<div class="warning custom-block"><p class="custom-block-title">不推荐这种方法</p><p>我们推荐使用 「方法 1：自动更新」 完成转换器更新。</p></div><ol><li><p>在 Zotero-编辑-设置-高级-文件和文件夹 找到自己的数据储存位置。</p><p>例如，下图中的数据储存位置就是 <code>E:\\Zotero File</code>，这里也可以点击「打开数据文件夹」快速打开你的数据文件夹。</p><p><img src="'+k+'" alt="数据储存位置" loading="lazy"></p></li><li><p>找到数据文件夹中的 translators 文件夹（对上图而言就是 <code>E:\\Zotero File\\translators</code>），这里是 Zotero 转换器 的存放位置。</p><p><img src="'+z+'" alt="转换器的存放位置" loading="lazy"></p></li><li><p>在 <a href="https://github.com/l0o0/translators_CN" target="_blank" rel="noreferrer">Zotero translators 中文维护小组</a> 下载最新的转换器。</p><p><img src="'+y+'" alt="手动在GitHub上下载转换器" loading="lazy"></p></li><li><p>将下载的 zip 文件解压后选择其中所有的转换器文件（.js 文件），并复制。</p><p><img src="'+B+'" alt="选择所有的转换器文件" loading="lazy"></p></li><li><p>将所有的转换器文件粘贴到第 1 步中 translators 文件夹并替换，此时已经完成 Zotero 中 转换器文件的更新。</p><p><img src="'+v+'" alt="替换旧的转换器" loading="lazy"></p></li><li><p>更新 <strong>每一个浏览器</strong> 中 Zotero Connector 扩展里的转换器（translators）。</p><div class="tip custom-block"><p class="custom-block-title">提示</p><p>从 Zotero Connector v5.0.124 开始，官方移除了 Zotero Connector 设置里 「Advanced」-&gt;「Translators」 中的 <code>Update Translaors</code> 按钮，如果你的浏览器扩展中仍然保留 <code>Update Translaors</code> 按钮，请务必先<a href="./../install#浏览器扩展-zotero-connector">升级 Zotero Connector 浏览器扩展</a>再进行后续操作。</p></div><p>请根据您使用的浏览器点开下面相应的说明，并按照说明中的步骤操作。（360（极速）浏览器、搜狗浏览器、QQ 浏览器等基于 Chromium 等国内厂商推出的浏览器请参照 Google Chrome 的步骤操作）</p><details class="details custom-block"><summary>Google Chrome、 Microsoft Edge 和 Mozilla Firefox</summary><ol><li><p>右键点击 Zotero Connector 按钮，然后点击 选项</p><p><img src="'+n+'" alt="打开 Zotero Connector 的选项" loading="lazy"></p></li><li><p>点击 Advanced 中的 Reset Translators 按钮</p><p><img src="'+l+'" alt="更新 Zotero Connector 中的 translators" loading="lazy"></p></li></ol></details><details class="details custom-block"><summary>Apple Safari</summary><p>虽然 Zotero 目前已经支持在 Safari 中使用，但实际使用中在 Safari 中抓取失败的案例比较多。 **建议使用 Microsoft Edge、Google Chrome 或 Mozilla Firefox 浏览器进行抓取。</p><ol><li><p>在网页空白处点鼠标右键，然后点击 Zotero Preference</p><p><img src="'+p+'" alt="打开 Zotero Connector 的选项" loading="lazy"></p></li><li><p>点击 Advanced 中的 Reset Translators 按钮</p><p><img src="'+l+'" alt="更新 Zotero Connector 中的 translators" loading="lazy"></p></li></ol></details><div class="warning custom-block"><p class="custom-block-title">警告</p><p><strong>这一步骤非常关键！</strong> 请务必确保为 <strong>每一个浏览器</strong> 中的 Zotero Connector 扩展更新转换器！</p><p>点击 Reset Translators 按钮时</p><p>你也可以通过检查日志的方式确保转换器成功更新</p><p><img src="'+c+'" alt="通过日志确保转换器成功更新" loading="lazy"></p></div></li></ol><p>此时你已经完成了转换器的更新，此时如果你的浏览器仍然不能完成对文献的识别，在确保步骤 1-5 正确的前提下，可重复几次步骤 6。</p>',3)),t("h2",U,[o[12]||(o[12]=r("卸载并重装 Zotero Connector 浏览器扩展 ")),a(s,{text:"中级"}),o[13]||(o[13]=r()),o[14]||(o[14]=t("a",{class:"header-anchor",href:"#卸载并重装-zotero-connector-浏览器扩展","aria-label":'Permalink to "卸载并重装 Zotero Connector 浏览器扩展 <Badge text="中级" />"'},"​",-1))]),o[24]||(o[24]=e('<p>如果按照上面的步骤更新后仍然无法正常抓取，建议卸载并重装最新版本的 Zotero Connector 浏览器扩展。</p><p>请根据您使用的浏览器点开下面相应的说明，并按照说明中的步骤操作。（360（极速）浏览器、搜狗浏览器、QQ 浏览器等基于 Chromium 等国内厂商推出的浏览器请参照 Google Chrome 的步骤操作）</p><details class="details custom-block"><summary>Google Chrome、 Microsoft Edge 和 Mozilla Firefox</summary><ol><li><p>进入浏览器的管理扩展程序页面</p><p>Google Chrome：点击浏览器界面右上角的 <code>三个点</code> 按钮，然后点击 「扩展程序」-&gt;「管理扩展程序」。</p><p>Microsoft Edge：点击浏览器界面右上角的 <code>三个点</code> 按钮，然后点击 「扩展」-&gt;「管理扩展」。</p><p>Mozilla Firefox：点击浏览器界面右上角的 <code>三横线</code> 按钮，然后点击 <code>扩展和主题</code>。</p><p><img src="'+x+'" alt="在 Chrome 中打开管理扩展程序" loading="lazy"></p></li><li><p>找到 Zotero Connector，点击 <code>移除</code> / <code>删除</code> / <code>三个点</code> 按钮里的 <code>移除</code>。</p><p><img src="'+A+'" alt="在扩展中移除 Zotero Connector" loading="lazy"></p></li><li><p>按照下面教程中的说明重新安装最新版的 Zotero Connector 浏览器扩展。</p><p><a href="./../install#浏览器扩展-zotero-connector">Zotero Connector 浏览器扩展安装说明</a></p></li></ol></details><details class="details custom-block"><summary>Apple Safari</summary><p>虽然 Zotero 目前已经支持在 Safari 中使用，但实际使用中在 Safari 中抓取失败的案例比较多。 **建议使用 Microsoft Edge、Google Chrome 或 Mozilla Firefox 浏览器进行抓取。</p><p>Safari 的浏览器扩展是跟随 Zotero 客户端一同安装的，重装 Zotero Connector 时通常需要卸载并重装 Zotero 客户端本体。</p><p>通常来说，仅删除 Zotero 客户端本体的执行文件不会影响到你的文库文献、设置、插件等数据，重新安装 Zotero 后所有内容都会保持以前的状态。如果不放心，你也可以先查看这一篇文档先备份一下 Zotero 数据文件和用户配置文件：<a href="./../backup">备份 Zotero 数据</a></p><ol><li><p>打开 Safari，点击系统菜单栏左上角的 <code>Safari 浏览器</code>，然后点击 <code>设置</code>。</p><p><img src="'+D+'" alt="打开 Safari 设置" loading="lazy"></p></li><li><p>在弹出的窗口中点击顶部的 <code>扩展</code> 选项卡，在左边栏找到 Zotero Connector，然后点击右边界面里的 <code>卸载</code>。</p><p><img src="'+_+'" alt="在扩展中点击卸载" loading="lazy"></p></li><li><p>点击弹窗中的 <code>在访达中显示</code>。</p><p><img src="'+S+'" alt="在访达中显示" loading="lazy"></p></li><li><p>完全退出 Safari 浏览器，然后回到上一步的访达界面中，将 Zotero 程序文件删除（如果不小心关闭了刚刚弹出的访达窗口，你也可以重新开启一个访达窗口，然后在左边栏中点击 <code>应用程序</code>，然后删除里面的 Zotero 程序文件）。</p></li></ol><p><img src="'+P+'" alt="删除 Zotero 程序文件" loading="lazy"></p><ol start="5"><li><p>重新从 Zotero 官网下载新的 Zotero 安装包并完成安装。<a href="./../install">获取 Zotero 下载链接</a></p></li><li><p>运行一次 Zotero，此时 Safari 的 Zotero Connector 扩展会自动安装。</p></li><li><p>重新运行 Safari 浏览器，如果没有看到 Zotero Connector 扩展图标，你可以在第 2 步的 <code>扩展</code> 选项卡中找到左边栏里的 Zotero Connector，确保它前面的复选框已经勾选。</p></li><li><p>如果 Zotero Connector 图标仍未出现，你可以在 Safari 窗口顶部的按钮区域空白处点鼠标右键，然后点击 <code>自定义工具栏...</code>，然后将下方的 Zotero Connector 图标拖到上方按钮区域里你希望放置的位置上。</p></li></ol><p><img src="'+T+'" alt="自定义工具栏" loading="lazy"></p><p><img src="'+j+'" alt="将 Zotero Connector 按钮拖至合适位置" loading="lazy"></p></details>',4)),t("h2",H,[o[15]||(o[15]=r("使用其他添加条目的方式 ")),a(s,{text:"初级"}),o[16]||(o[16]=r()),o[17]||(o[17]=t("a",{class:"header-anchor",href:"#使用其他添加条目的方式","aria-label":'Permalink to "使用其他添加条目的方式 <Badge text="初级" />"'},"​",-1))]),o[25]||(o[25]=t("p",null,[r("如果以上所有操作都未能解决你遇到的抓取问题，可能你访问的网页目前无法通过 Zotero Connector 抓取文献信息。你可以尝试换其他网站进行抓取，或者改用 "),t("a",{href:"./../add-items#通过附件添加条目-推荐"},"通过附件添加条目"),r("、"),t("a",{href:"./../add-items#由通用格式的引用信息导入-通用方法"},"由通用格式的引用信息导入"),r(" 以及 "),t("a",{href:"./../add-items#手动创建条目-万能方法"},"手动创建条目"),r(" 等备用方式。详细操作步骤请阅读 "),t("a",{href:"./../add-items"},"添加条目教程"),r(" 。")],-1)),a(d),a(m)])}const K=f(G,[["render",V]]);export{X as __pageData,K as default};
